// src/components/ProductForm.js
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function ProductForm() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    quantity: '',
  });

  const { id } = useParams(); // Get product ID from URL if available (for editing)
  const navigate = useNavigate(); // Hook for page navigation

  // Fetch product data if editing
  useEffect(() => {
    if (id) {
      const fetchProduct = async () => {
        try {
          const response = await axios.get(`http://localhost:5002/products/${id}`);
          setFormData(response.data); // Populate form with product data
        } catch (error) {
          console.error('Error fetching product:', error);
          alert('Failed to fetch product. Please try again.');
        }
      };
      fetchProduct();
    }
  }, [id]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page reload
  
    console.log('Form data:', formData); // Log the form data
  
    try {
      if (id) {
        console.log('Updating product...');
        const response = await axios.put(`http://localhost:5002/products/${id}`, formData);
        console.log('Product updated:', response.data);
        alert('Product updated successfully!');
      } else {
        console.log('Adding new product...');
        const response = await axios.post('http://localhost:5002/products', formData);
        console.log('Product added:', response.data);
        alert('Product added successfully!');
      }
  
      navigate('/'); // Redirect to product list
    } catch (error) {
      console.error('Error saving product:', error.response || error.message);
      alert(`Error saving product: ${error.response ? error.response.data : error.message}`);
    }
  };
  

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label className="form-label">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Description</label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Price</label>
        <input
          type="number"
          name="price"
          value={formData.price}
          onChange={handleInputChange}
          className="form-control"
          min="0"
          required
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Quantity</label>
        <input
          type="number"
          name="quantity"
          value={formData.quantity}
          onChange={handleInputChange}
          className="form-control"
          min="0"
          required
        />
      </div>
      <button type="submit" className="btn btn-success">
        {id ? 'Update Product' : 'Add Product'}
      </button>
    </form>
  );
}

export default ProductForm;
