// src/components/ProductForm.js
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const response = await axios.get('http://localhost:5002/products');

function ProductForm() {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    quantity: '',
  });

  const { id } = useParams();  // Retrieve product ID from URL params (if editing)
  const navigate = useNavigate();  // Hook to navigate between pages

  // Fetch product data if editing
  useEffect(() => {
    if (id) {
      const fetchProduct = async () => {
        try {
        //   const response = await axios.get(`http://localhost:5002/products/${id}`);
          const response = await axios.get('http://localhost:5002/products');
          setFormData(response.data);
        } catch (error) {
          console.error('Error fetching product:', error);
        }
      };
      fetchProduct();
    }
  }, [id]);  // Only re-run if 'id' changes

  // Handle form field changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

//   // Handle form submission
//   const handleSubmit = async (e) => {
//     e.preventDefault();  // Prevent page reload on form submission
//     try {
//       if (id) {
//         // Update existing product
//         await axios.put(`http://localhost:5002/products/${id}`, formData);
//       } else {
//         // Add new product
//         await axios.post('http://localhost:5002/products', formData);
//       }
//       navigate('/');  // Redirect back to product list after submission
//     } catch (error) {
//       console.error('Error saving product:', error);
//     }
//   };

const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page refresh
  
    console.log('Form data:', formData); // Log the form data
  
    try {
      if (id) {
        console.log('Updating product...');
        await axios.put(`http://localhost:5002/products/${id}`, formData);
      } else {
        console.log('Adding new product...');
        await axios.post('http://localhost:5002/products', formData);
      }
  
      console.log('Redirecting to home...');
      navigate('/'); // Attempt to navigate to the home page
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };
  
  

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label className="form-label">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Description</label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Price</label>
        <input
          type="number"
          name="price"
          value={formData.price}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Quantity</label>
        <input
          type="number"
          name="quantity"
          value={formData.quantity}
          onChange={handleInputChange}
          className="form-control"
          required
        />
      </div>
      <button type="submit" className="btn btn-success">
        {id ? 'Update Product' : 'Add Product'}
      </button>
    </form>
  );
}

export default ProductForm;
